BEGIN
    DECLARE @StartDate DATE;
    DECLARE @EndDate DATE;

    -- Call the GetWeekStartDate function to get the start date of the week
    SET @StartDate = dbo.GetWeekStartDate(@WeekNumber, @Year);

    -- Calculate end date of the week
    SET @EndDate = DATEADD(DAY, 6, @StartDate);

    -- If the end date falls into the next year, adjust it to the last day of the current year
    IF YEAR(@EndDate) > @Year
    BEGIN
        SET @EndDate = DATEFROMPARTS(@Year, 12, 31);
    END

    RETURN @EndDate;
END;

CREATE FUNCTION GetWeekEndDate (
    @WeekNumber INT,
    @Year INT
)
RETURNS DATE
AS

BEGIN
    DECLARE @FirstDayOfYear DATE;
    SET @FirstDayOfYear = DATEFROMPARTS(@Year, 1, 1);

    DECLARE @StartDate DATE;

    -- Calculate start date of the week
    SET @StartDate = DATEADD(WEEK, @WeekNumber - 1, @FirstDayOfYear);
    SET @StartDate = DATEADD(DAY, 1 - DATEPART(WEEKDAY, @StartDate), @StartDate);

    -- If the start date falls into the next year, adjust it to the last day of the current year
    IF YEAR(@StartDate) > @Year
    BEGIN
        SET @StartDate = DATEFROMPARTS(@Year, 12, 31);
    END

    RETURN @StartDate;
END;

